@testset "Running test case1 for CosineTest" begin
  using CSV, DataFrames, Plots

  @mtkbuild model = CosineTest()
  u0 = []
  prob = ODEProblem(model, u0, (0, 5))
  sol = solve(prob, DefaultODEAlgorithm())
  @test SciMLBase.successful_retcode(sol)
  @test sol[model.signal.y][1] ≈ 0.5 atol=0.001
  @test sol[model.signal.y][end] ≈ -0.49999 atol=0.001
  @test sol[model.integrator.x][end] ≈ 2.45255 atol=0.001
# Signals selected for regression testing: ["signal.y","integrator.x"]
  ref_times = [sol(t, idxs=:t) for t in LinRange(sol[:t][1], sol[:t][end], 100)]
  if get(ENV, "DYAD_UPDATE_REFS", "") !== ""
    # If asked to update snapshots, write out reference data for all signals
    mkpath("snapshots")
    CSV.write("snapshots/CosineTest_case1_sig0.ref", DataFrame(t=ref_times, expected=[sol(t, idxs=model.signal.y) for t in ref_times]))
    CSV.write("snapshots/CosineTest_case1_sig1.ref", DataFrame(t=ref_times, expected=[sol(t, idxs=model.integrator.x) for t in ref_times]))
  end
    if isfile("snapshots/CosineTest_case1_sig0.ref")
      ref = CSV.read("snapshots/CosineTest_case1_sig0.ref", DataFrame)
      [@test ref.expected[i] ≈ sol(ref.t[i], idxs=model.signal.y) atol=0.001 for i in 1:length(ref.expected)]
      if get(ENV, "DYAD_COMPARISONS", "") !== ""
        df = DataFrame(t=sol[:t], actual=sol[model.signal.y])
        dfr = CSV.read("snapshots/CosineTest_case1_sig0.ref", DataFrame)
        plot(sol, idxs=[model.signal.y], width=2, label="Actual value of signal.y")
        scatter!(dfr.t, dfr.expected, mc=:red, ms=3, label="Expected value of signal.y")
        plot!([df.t[1]], [0.5], seriestype=:scatter, label="Initial Condition for signal.y")
        plot!([df.t[end]], [-0.49999], seriestype=:scatter, label="Final Condition for signal.y")
        mkpath("comparisons")
        savefig("comparisons/CosineTest_case1_sig0.png")
      end
    else
      mkpath("snapshots")
      CSV.write("snapshots/CosineTest_case1_sig0.ref", DataFrame(t=ref_times, expected=[sol(t, idxs=model.signal.y) for t in ref_times]))
    end
    if isfile("snapshots/CosineTest_case1_sig1.ref")
      ref = CSV.read("snapshots/CosineTest_case1_sig1.ref", DataFrame)
      [@test ref.expected[i] ≈ sol(ref.t[i], idxs=model.integrator.x) atol=0.001 for i in 1:length(ref.expected)]
      if get(ENV, "DYAD_COMPARISONS", "") !== ""
        df = DataFrame(t=sol[:t], actual=sol[model.integrator.x])
        dfr = CSV.read("snapshots/CosineTest_case1_sig1.ref", DataFrame)
        plot(sol, idxs=[model.integrator.x], width=2, label="Actual value of integrator.x")
        scatter!(dfr.t, dfr.expected, mc=:red, ms=3, label="Expected value of integrator.x")
        plot!([df.t[end]], [2.45255], seriestype=:scatter, label="Final Condition for integrator.x")
        mkpath("comparisons")
        savefig("comparisons/CosineTest_case1_sig1.png")
      end
    else
      mkpath("snapshots")
      CSV.write("snapshots/CosineTest_case1_sig1.ref", DataFrame(t=ref_times, expected=[sol(t, idxs=model.integrator.x) for t in ref_times]))
