@testset "Running test case1 for StepTest" begin
  using CSV, DataFrames, Plots

  @mtkbuild model = StepTest()
  u0 = []
  prob = ODEProblem(model, u0, (0, 1))
  sol = solve(prob, DefaultODEAlgorithm())
  @test SciMLBase.successful_retcode(sol)
  @test sol[model.signal.y][1] ≈ 0.5
  @test sol[model.signal.y][end] ≈ 1.9
  @test sol[model.terminator.u][end] ≈ 1.9
# Signals selected for regression testing: ["signal.y","terminator.u"]
  ref_times = [sol(t, idxs=:t) for t in LinRange(sol[:t][1], sol[:t][end], 100)]
  if get(ENV, "DYAD_UPDATE_REFS", "") !== ""
    # If asked to update snapshots, write out reference data for all signals
    mkpath("snapshots")
    CSV.write("snapshots/StepTest_case1_sig0.ref", DataFrame(t=ref_times, expected=[sol(t, idxs=model.signal.y) for t in ref_times]))
    CSV.write("snapshots/StepTest_case1_sig1.ref", DataFrame(t=ref_times, expected=[sol(t, idxs=model.terminator.u) for t in ref_times]))
  end
    if isfile("snapshots/StepTest_case1_sig0.ref")
      ref = CSV.read("snapshots/StepTest_case1_sig0.ref", DataFrame)
      [@test ref.expected[i] ≈ sol(ref.t[i], idxs=model.signal.y) for i in 1:length(ref.expected)]
      if get(ENV, "DYAD_COMPARISONS", "") !== ""
        df = DataFrame(t=sol[:t], actual=sol[model.signal.y])
        dfr = CSV.read("snapshots/StepTest_case1_sig0.ref", DataFrame)
        plot(sol, idxs=[model.signal.y], width=2, label="Actual value of signal.y")
        scatter!(dfr.t, dfr.expected, mc=:red, ms=3, label="Expected value of signal.y")
        plot!([df.t[1]], [0.5], seriestype=:scatter, label="Initial Condition for signal.y")
        plot!([df.t[end]], [1.9], seriestype=:scatter, label="Final Condition for signal.y")
        mkpath("comparisons")
        savefig("comparisons/StepTest_case1_sig0.png")
      end
    else
      mkpath("snapshots")
      CSV.write("snapshots/StepTest_case1_sig0.ref", DataFrame(t=ref_times, expected=[sol(t, idxs=model.signal.y) for t in ref_times]))
    end
    if isfile("snapshots/StepTest_case1_sig1.ref")
      ref = CSV.read("snapshots/StepTest_case1_sig1.ref", DataFrame)
      [@test ref.expected[i] ≈ sol(ref.t[i], idxs=model.terminator.u) for i in 1:length(ref.expected)]
